DROP VIEW IF EXISTS `risk_answer`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `risk_answer` AS select `a`.`uid` AS `uid`,`a`.`pid` AS `pid`,`q`.`category` AS `category`,`c`.`value` AS `value`,count(`a`.`qid`) AS `num` from ((`tbl_answer` `a` left join `tbl_data2` `q` on((`q`.`id` = `a`.`qid`))) left join `tbl_category` `c` on(((`c`.`id` = `q`.`category`) and (`c`.`type` = 'risk')))) group by `a`.`uid`,`a`.`pid`,`q`.`category`,`c`.`value` ;

-- ----------------------------
-- View structure for risk_cate
-- ----------------------------
DROP VIEW IF EXISTS `risk_cate`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `risk_cate` AS select `c1`.`id` AS `id`,`c1`.`title` AS `title`,`c1`.`value` AS `value`,`c1`.`pid` AS `pid`,`c1`.`status` AS `status`,`c1`.`modified` AS `modified`,`c1`.`created` AS `created`,sum(`rq`.`num`) AS `total_num`,count(`rq`.`id`) AS `child_num`,count(`q`.`id`) AS `quest_num`,`c1`.`sorting` AS `sorting`,`c1`.`alias` AS `alias` from ((`tbl_category` `c1` left join `risk_quest` `rq` on((`rq`.`value` like concat(`c1`.`value`,'>%')))) left join `tbl_data2` `q` on((`c1`.`id` = `q`.`category`))) where (`c1`.`type` = 'risk') group by `c1`.`id`,`c1`.`title`,`c1`.`value`,`c1`.`pid`,`c1`.`status`,`c1`.`modified`,`c1`.`created` ;

-- ----------------------------
-- View structure for risk_quest
-- ----------------------------
DROP VIEW IF EXISTS `risk_quest`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `risk_quest` AS select `c`.`value` AS `value`,`c`.`id` AS `id`,count(`q`.`id`) AS `num` from (`tbl_category` `c` left join `tbl_data2` `q` on((`c`.`id` = `q`.`category`))) where (`c`.`type` = 'risk') group by `c`.`id`,`c`.`value` ;
